import { useContext, useEffect, useState } from 'react';
import AppBar from '@mui/material/AppBar';
import { observer } from 'mobx-react-lite'
import Toolbar from '@mui/material/Toolbar';
import Typography from '@mui/material/Typography';
import { TextField, Paper, Button, Grid } from "@material-ui/core";


import { Context } from '.';
import AddTodoForm from "./components/AddTodoForm";
import List from "../components/List";
import LoginForm from './components/LoginForm';

import ExitToAppIcon from '@mui/icons-material/ExitToApp';
import './App.css';

const styles = {
  Paper: {
    padding: 20,
    margin: "auto",
    textAlign: "center",
    width: 500
  }
};

function App() {
  const [list, setList] = useState({});
  const { store } = useContext(Context);

  useEffect(() => {
    if (localStorage.getItem('token')) {
      store.checkAuth();
    }
  }, []);

  if (store.isLoading){
    return (
      <div>...Loading</div>
    )
  }

  if (!store.isAuth) {
    return (
      <LoginForm />
    )
  }
  const addToList = todo => {
    let locaList = list;
    locaList[`todo${Date.now()}`] = {
      todo: todo,
      status: "active"
    };

    setList({ locaList });
  };
  const deleteTodo = key => {
    let localList = list;
    localList[key] = null;

    setList({ localList });
  };
  const updateTodo = key => {
    let localList = list;
    localList[key]["status"] = "editing";

    setList({ localList });
  };
  const saveTodo = (key, todo) => {
    let localList = list;
    localList[key] = {
      todo: todo,
      status: "active"
    };

    setList({ localList });
  };

  return (
    <div>
      <Toolbar sx={{ borderBottom: 1, borderColor: 'divider' }}>
        <Typography  variant="subtitle1" color={ store.user.isActivated ? 'primary' : 'error' }>
          { store.user.isActivated ? 'Аккаунт подтвержден' : 'Аккаунт не подтвержден' }
        </Typography>
        <Typography
          component="h2"
          variant="h5"
          color="inherit"
          align="center"
          noWrap
          sx={{ flex: 1 }}
        >
         { `Добро пожаловать ${ store.user.email  }` }
        </Typography>
        <Button variant="outlined" size="small" onClick={ () => store.logout() }>
          <ExitToAppIcon/>
          Выйти из аккаунта
        </Button>
      </Toolbar>
      <Toolbar
        component="nav"
        variant="dense"
        sx={{ justifyContent: 'space-between', overflowX: 'auto' }}
      >
      </Toolbar> 
      <Grid container spacing={0}>
          <Grid item xs={12}>
            <Paper style={styles.Paper}>
              <AddTodoForm addToList={addToList} />
            </Paper>
          </Grid>
          <Grid item xs={12} style={styles.Paper}>
            <List
              deleteTodo={deleteTodo}
              list={list}
              updateTodo={updateTodo}
              saveTodo={saveTodo}
            />
          </Grid>
        </Grid>     
    </div>
  );
}

export default observer(App);
