import { useContext, useEffect } from 'react';
import { Context } from '.';
import './App.css';
import LoginForm from './components/LoginForm';
import { observer } from 'mobx-react-lite'


function App() {
  const { store } = useContext(Context);

  useEffect(() => {
    if (localStorage.getItem('token')) {
      store.checkAuth();
    }
  }, []);

  if (store.isLoading){
    return (
      <div>...Loading</div>
    )
  }

  return (
    <div>
      <h1>{ store.isAuth?  `Вы зарегистрированы как ${ store.user.email  }` :   <LoginForm /> }</h1>
      <h1>{ store.user.isActivated ? 'Аккаунт подтвержден' : 'Пройдите верификацию' }</h1>
      <button onClick={ () => store.logout() }>Выйти из аккаунта</button>
    </div>
  );
}

export default observer(App);
